import { AppProvider, Page, ResourceList, ResourceItem,TextStyle,Link,Stack,Card, Avatar} from '@shopify/polaris';
import { ResourcePicker, TitleBar } from '@shopify/app-bridge-react';
import axios from 'axios';
const img = 'https://cdn.shopify.com/s/files/1/0757/9955/files/empty-state.svg';
import Router from 'next/router'
import Cookies from 'js-cookie';
import translations from '@shopify/polaris/locales/en.json';
class Index extends React.Component {
  constructor(props) {
  super(props);
  this.state = { open: false, selectedItems: [], items:[], loading:true };
  this.setSelectedItems = this.setSelectedItems.bind(this);
}
componentDidMount(){
  axios.get('/allbars').then(response => {
      this.setState({
        items: response.data,
        loading: false
      })
    })
}
  render() {
    return (
      <Page
        title={'Shipping bar'}
          primaryAction={{
            content: 'Create New Bar',
            onAction: () => this.createnewbar()
          }}
          >
          <Card  sectioned>
          <ResourceList
    resourceName={{singular: 'customer', plural: 'customers'}}
    items={[
      {
        id: 341,
        url: 'customers/341',
        name: 'Mae Jemison',
        location: 'Decatur, USA',
        latestOrderUrl: 'orders/1456',
      },
      {
        id: 256,
        url: 'customers/256',
        name: 'Ellen Ochoa',
        location: 'Los Angeles, USA',
        latestOrderUrl: 'orders/1457',
      },
    ]}
    renderItem={(item) => {
      const {id, url, name, location, latestOrderUrl} = item;
      const media = <Avatar customer size="medium" name={name} />;
      const shortcutActions = latestOrderUrl
        ? [
            {
              content: 'View latest order',
              url: latestOrderUrl,
            },
          ]
        : null;

      return (
        <ResourceItem
          id={id}
          url={url}
          media={media}
          shortcutActions={shortcutActions}
          persistActions
        >
          <h3>
            <TextStyle variation="strong">{name}</TextStyle>
          </h3>
          <div>{location}</div>
        </ResourceItem>
      );
    }}
  />
          <ResourceList
            resourceName={{singular: 'Shipping bar', plural: 'Shipping bars'}}
            loading={this.state.loading}
            items={this.state.items}
            renderItem={(item)=> {
              const {id, name, intial_msg, goal} = item;
              const media = <Avatar customer size="medium" name={name} />;
              const shortcutActions = name
                  ? [
                      {
                        content: 'View latest order',
                        accessibilityLabel: `View ${name}’s latest order`,
                        url: name,
                      },
                    ]
                  : null;
                return (
                  <ResourceItem
                    id={id}
                    media={media}
                    name={name}
                    shortcutActions={shortcutActions}
                    persistActions
                  >
                    <div onClick={(ev) =>this.editbar(id)}> 
                    <Stack>
                      <Stack.Item fill>
                        <TextStyle variation="strong">{name}</TextStyle>
                      </Stack.Item>
                    </Stack>   
                    </div>  
                  </ResourceItem>
                );
            }}  
          />
          </Card>
      </Page>
    );
  } 

  setSelectedItems = (ev)=>{
    this.setState({selectedItems: ev});
  }
  createnewbar= () =>{
    this.setState({
        loading: true
      });
    let path = '/createbar';
    Router.push(path);
  }
  editbar = (id) =>{
    this.setState({
        loading: true
      })
    localStorage.setItem('bar_id', id);
    let path = '/editbar';
    Router.push(path);
    // this.props.history.push(path);
  }
}

export default Index;
