import { EmptyState, Layout, Page,Card, Form, FormLayout,TextStyle,Checkbox,
  TextField,Button, Avatar, RangeSlider} from '@shopify/polaris';
import { ResourcePicker, TitleBar } from '@shopify/app-bridge-react';
import axios from 'axios';
const img = 'https://cdn.shopify.com/s/files/1/0757/9955/files/empty-state.svg';

class Createbar extends React.Component {
  constructor(props) {
  super(props);
  this.state = { open: false, selectedItems: [], items:[],
  spt_color:'',
  txt_color:'',
  bg_color:'',
  opacity:0, };
 this.handleNewsLetterChange = this.handleNewsLetterChange.bind(this);
 this.handleEmailChange = this.handleEmailChange.bind(this);
}
componentDidMount(){
  // axios.get('/allbars').then(response => {
  //   console.log(response);
  //     this.setState({
  //       items: response.data
  //     })
  //   })
}
  render() {
    const {spt_color,txt_color,bg_color,opacity} = this.state;
    return (
      <Page
          primaryAction={{
            content: 'Back',
            url: '/'
          }}
          >
      <Card sectioned title="Create new bar">
        <Form onSubmit={this.handleSubmit}>
          <Card.Section title="Style Configuration">
          <FormLayout>
            <FormLayout.Group>
            <TextField
            value={bg_color}
            onChange={this.handleEmailChange}
            label="Background Color"
            type="color"
          />
          <TextField
            value={txt_color}
            onChange={this.handleEmailChange}
            label="Text Color"
            type="color"
          />
          <TextField
            value={spt_color}
            onChange={this.handleEmailChange}
            label="Special Text Color"
            type="color"
          />
      </FormLayout.Group>
      <RangeSlider
        label="Background Color Opacity"
        value={opacity}
        onChange={this.handleRangeSliderChange}
        min={0}
        max={9}
        output
        helpText={
              <span>
                The range is from 0 to 1,0 = transparent and 1 = solid
               </span>
            }
      />
      
      </FormLayout>
      </Card.Section>
        <Card.Section title="Content Configuration">
          <FormLayout>
            <TextField
              value={'aaa@gmail.com'}
              onChange={this.handleEmailChange}
              label="Email"
              type="email"
              helpText={
                <span>
                  Weâ€™ll use this email address to inform you on future changes to
                  Polaris.
                </span>
              }
            />

            <Button submit>Submit</Button>
        </FormLayout>
      </Card.Section>
    </Form>
    </Card>
    </Page>
        )
      }
      handleNewsLetterChange = (ev)=>{
    this.setState({selectedItems: ev});
  }
  handleEmailChange = (id) =>{
    console.log(id);
