import { EmptyState, Layout, Page,Card, Form, FormLayout,TextStyle,Checkbox,
  TextField,Button, Avatar, RangeSlider, DropZone,Stack,Thumbnail} from '@shopify/polaris';
import { ResourcePicker, TitleBar } from '@shopify/app-bridge-react';
import axios from 'axios';
const img = 'https://cdn.shopify.com/s/files/1/0757/9955/files/empty-state.svg';

class Createbar extends React.Component {
  constructor(props) {
  super(props);
  this.state = { open: false, selectedItems: [], items:[],
  spt_color:'',
  txt_color:'',
  bg_color:'',
  opacity:0,
  bg_img:'',
  filename:'' };
 this.handleSubmit = this.handleSubmit.bind(this);
 this.handlecolorChange = this.handlecolorChange.bind(this);
}
componentDidMount(){
  // axios.get('/allbars').then(response => {
  //   console.log(response);
  //     this.setState({
  //       items: response.data
  //     })
  //   })
}
  render() {
    const {spt_color,txt_color,bg_color,opacity,filename,bg_img} = this.state;
    const fileUpload = !this.state.bg_img && <DropZone.FileUpload />;

        const uploadedFile = bg_img && (<Stack><Thumbnail
            size="small"
            alt={bg_img.name}
            source='https://cdn.shopify.com/s/files/1/0757/9955/files/New_Post.png?12678548500147524304'
            />
            <div>
              {bg_img.name}
            </div>
          </Stack>
      );
    return (
      <Page
          primaryAction={{
            content: 'Back',
            url: '/'
          }}
          >
      <Card sectioned title="Create new bar">
        <Form onSubmit={this.handleSubmit}>
          <Card.Section title="Style Configuration">
          <FormLayout>
            <FormLayout.Group>
            <TextField
            value={bg_color}
            onChange={(ev) =>this.handlecolorChange(ev, 'bg_color')}
            label="Background Color"
            type="color"
          />
          <TextField
            value={txt_color}
            onChange={(ev) =>this.handlecolorChange(ev, 'txt_color')}
            label="Text Color"
            type="color"
          />
          <TextField
            value={spt_color}
            onChange={(ev) =>this.handlecolorChange(ev, 'spt_color')}
            label="Special Text Color"
            type="color"
          />
      </FormLayout.Group>
      <RangeSlider
        label="Background Color Opacity"
        value={opacity}
        onChange={(ev) =>this.handlecolorChange(ev, 'opacity')}
        min={0}
        max={9}
        output
        helpText={
              <span>
                The range is from 0 to 1,0 = transparent and 1 = solid
               </span>
            }
      />
      <DropZone onDrop={this.handleDropZoneDrop}>
      {uploadedFile}
      {fileUpload}
      </DropZone>
      </FormLayout>
      </Card.Section>
        <Card.Section title="Content Configuration">
          <FormLayout>
            <TextField
              value={'aaa@gmail.com'}
              onChange={this.handleEmailChange}
              label="Email"
              type="email"
              helpText={
                <span>
                  We’ll use this email address to inform you on future changes to
                  Polaris.
                </span>
              }
            />
        </FormLayout>
      </Card.Section>
      <Card.Section>
      <Button submit>Submit</Button>
      </Card.Section>
    </Form>
    </Card>
    </Page>
        )
      }
      handleNewsLetterChange = (ev)=>{
        this.setState({selectedItems: ev});
      }
  handlecolorChange = (ev, name) =>{
    this.setState({
    ...this.state,
    [name]: ev
  });
  }
  handleSubmit = (ev) =>{
    //
  }
  handleDropZoneDrop= (ev) =>{
    console.log(ev);
    this.setState({ bg_img : ev[0]});
    this.createImage(ev[0]);
  }
  createImage(file) {
    let reader = new FileReader();
    reader.onload = (e) => {
      this.setState({
        bg_img: e.target.result
      })
    };
    reader.readAsDataURL(file);  
  }
  }
export default Createbar;
