import { EmptyState, Layout, Page, Form, FormLayout, TextField, Button } from '@shopify/polaris';
import { ResourcePicker, TitleBar } from '@shopify/app-bridge-react';
import axios from 'axios';
const img = 'https://cdn.shopify.com/s/files/1/0757/9955/files/empty-state.svg';

class Index extends React.Component {
  state = { open: false, shop: '' };
  render() {
    return (
      <Page>
        <TitleBar primaryAction={{
          content: 'Select products',
          onAction: () => this.setState({ open: true }),
        }} />
        <ResourcePicker
          resourceType="Product"
          showVariants={false}
          open={this.state.open}
          onSelection={(resources) => this.handleSelection(resources)}
          onCancel={() => this.setState({ open: false })}
        />
        <Layout>
        <Form onSubmit={this.handleSubmit}>
          <FormLayout>
        <TextField
          value={this.state.shop}
          onChange={this.handleshopChange}
          label="Enter your shopify domain to login"
          type="text"
        />
        <Button submit>Submit</Button>
        </FormLayout>
        </Form>
          
        </Layout>
      </Page>
    );
  }

  handleSelection = (resources) => {
    const idsFromResources = resources.selection.map((product) => product.id);
    this.setState({ open: false });
    console.log(idsFromResources)
  };

  handleshopChange = (event) =>{
    console.log(event);
    this.setState({ shop: event });
  }

  handleSubmit= (event) =>{
    axios.post('/auth', {'shop' :this.state.shop}).then(response => {
      console.log(response);
      })
  }
}

export default Index;
