import { EmptyState, Layout, Page, ResourceList, ResourceItem,TextStyle,Link,Stack,Card, Avatar} from '@shopify/polaris';
import { ResourcePicker, TitleBar } from '@shopify/app-bridge-react';
import axios from 'axios';
const img = 'https://cdn.shopify.com/s/files/1/0757/9955/files/empty-state.svg';

class Index extends React.Component {
  constructor(props) {
  super(props);
  this.state = { open: false, selectedItems: [], items:[] };
  this.setSelectedItems = this.setSelectedItems.bind(this);
}
componentDidMount(){
  console.log("Mount");
  axios.get('/allbars').then(response => {
    console.log(response);
      this.setState({
        items: response.data
      })
    })
}
  render() {
    return (
      <Page
          primaryAction={{
            content: 'Create New Bar',
            url: '/annotated-layout'
          }}
          >
          <Card title={'Shipping bar'} sectioned>
          <ResourceList
            items={this.state.items}
            renderItem={this.renderItem}
            resourceName={{singular: 'Shipping bar', plural: 'Shipping bars'}}
          />
          <ResourceList
            resourceName={{singular: 'Shipping bar', plural: 'Shipping bars'}}
            items={this.state.items}
            selectedItems={this.state.selectedItems}
            onSelectionChange={this.setSelectedItems}
            renderItem={(item) => {
              const {id, url, name} = item;
              return (
                <ResourceItem
                  id={id}
                  url={url}
                  accessibilityLabel={`View details for ${name}`}
                  name={name}
                >
                <div onClick={() => this.editbar(id)}> 
                  <Stack>
                    <Stack.Item fill>
                    <TextStyle variation="strong">{name}</TextStyle>
                   </Stack.Item>
                      <Stack.Item>
                        
                      </Stack.Item>
                    </Stack>   
                  </div>  
                </ResourceItem>
              );
            }}
          />
          </Card>
      </Page>
    );
  }
   renderItem = (item)=> {
    const {id, name} = item;
    const media = <Avatar customer size="medium" name={name} />;
              return (
                <ResourceItem
                  id={id}
                  media={media}
                  name={name}
                >
                <div onClick={() => this.editbar(id)}> 
                  <Stack>
                    <Stack.Item fill>
                    <TextStyle variation="strong">{name}</TextStyle>
                   </Stack.Item>
                      <Stack.Item>
                        
                      </Stack.Item>
                    </Stack>   
                  </div>  
                </ResourceItem>
              );
  }

  handleSelection = (resources) => {
    const idsFromResources = resources.selection.map((product) => product.id);
    this.setState({ open: false });
    console.log(idsFromResources)
  };
  setSelectedItems = (ev)=>{
    this.setState({selectedItems: ev});
  }
  editbar = (id) =>{
    console.log(id);
  }
}

export default Index;
